import React, { useContext } from "react";
import NextButton from "../NextButton/NextButton";
import { ReactComponent as Webcam } from "./Assets/Webcam.svg";
import { ReactComponent as FaceToFace } from "./Assets/FaceToFace.svg";
import { ProgressContext } from "../../contexts/ProgressContext";

const Page5 = (props) => {
  const { meetingType, setMeetingType } = useContext(ProgressContext);
  const handleSol = () => {
    switch (mentoringType) {
      case 0:
        return <Solo onClick={() => setMentoringType(1)} />;
      case 1:
        return <SoloGreen onClick={() => setMentoringType(0)} />;
      case 2:
        return <Solo onClick={() => setMentoringType(3)} />;
      case 3:
        return <SoloGreen onClick={() => setMentoringType(2)} />;
    }
  };
  const handleGroup = () => {
    switch (mentoringType) {
      case 0:
        return <Group onClick={() => setMentoringType(2)} />;
      case 1:
        return <Group onClick={() => setMentoringType(3)} />;
      case 2:
        return <GroupGreen onClick={() => setMentoringType(0)} />;
      case 3:
        return <GroupGreen onClick={() => setMentoringType(1)} />;
    }
  };
  return (
    <div>
      <div className="pageFiveContent">
        <h2>Où se déroule votre mentorat ?</h2>
        <h3>Vous pouvez sélectionner les deux ✌️</h3>
        <ul className="meetingTypeContainer">
          <li>
            <Webcam />
          </li>
          <li>
            <FaceToFace />
          </li>
        </ul>
      </div>
      <NextButton />
    </div>
  );
};

export default Page5;
