import React, { useContext } from "react";
import NextButton from "../NextButton/NextButton";
import { ProgressContext } from "../../contexts/ProgressContext";

const Page2 = (props) => {
  const { job, setSkills } = useContext(ProgressContext);
  let tempSkills = [];
  const handleSkills = (event) => {
    tempSkills.push(event.target.innerText);
    console.log(tempSkills);
  };

  return (
    <div>
      <div className="pageTwoContent">
        <h2>Super un.e nouveau.elle {job} </h2>
        <h3>Optionnel : Ajoutez des compétences associées </h3>
        <section className="skillsContainer">
          <div
            onClick={(event) => {
              handleSkills(event);
              setSkills(tempSkills);
            }}
          >
            Figma
          </div>
          <div
            onClick={(event) => {
              handleSkills(event);
            }}
          >
            Atomic Design
          </div>
          <div
            onClick={(event) => {
              handleSkills(event);
            }}
          >
            Sketch
          </div>
          <div
            onClick={(event) => {
              handleSkills(event);
            }}
          >
            Photoshop
          </div>
          <div
            onClick={(event) => {
              handleSkills(event);
            }}
          >
            UX Design
          </div>
          <div
            onClick={(event) => {
              handleSkills(event);
            }}
          >
            Lightroom
          </div>
        </section>
      </div>

      <NextButton />
    </div>
  );
};

export default Page2;
