    if (percentage < 100) {
      setPercentage(percentage + 10);
    }
  };
  return (
    <ProgressContext.Provider
      value={{ percentage, increasePercentage, infos, setJob }}
    >
      {props.children}
    </ProgressContext.Provider>
  );
};

export default ProgressContextProvider;
