import React, { useEffect, useContext } from "react";
import { ProgressContext } from "../../contexts/ProgressContext.js";

const RegisterButton = (props) => {
  const { firsName, lastName, job, city, resume, skills } = useContext(
    ProgressContext
  );

  const sendRequest = useEffect(() => {
    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        firsName: firsName,
        lastName: lastName,
        jobTitle: job,
        city: city,
        course: resume,
        skills: skills,
      }),
    };
    fetch("http://localhost:8000/apip/posts", requestOptions)
      .then((response) => response.json())
      .then((data) => setPostId(data.id));
  }, []);

  return (
    <div className="RegisterButtonContainer">
      <button type="submit" className="RegisterButton" onClick={sendRequest}>
        S'inscrire
      </button>
    </div>
  );
};

export default RegisterButton;
